fun isValid(s: String): String {
    val charMap = HashMap<Char, Int>()

    var changedMoreThanOne = false
    s.forEach {
        val newValue = charMap.getOrDefault(it, 0) + 1
        charMap[it] = newValue
    }
    val charCount = charMap[s[0]] ?: 0
    charMap.forEach {
        if (charCount != it.value) {
            if (Math.abs(charCount.minus(it.value)) == 1 || it.value == 1) {
                if (changedMoreThanOne) {
                    return "NO"
                } else {
                    changedMoreThanOne = true
                }
            } else {
                return "NO"
            }
        }
    }
    return "YES"
}

fun main(args: Array<String>) {

    val result = isValid(
        ""
    )

    println(result)
}
